INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-2', 'Em qual opção o ponto e vírgula é usado corretamente para introduzir uma explicação?', 'O ponto e vírgula pode ser usado antes de expressões explicativas como "isto é", "ou seja", "a saber".', 2),
('ponto-virgula-2', 'Identifique a alternativa em que o ponto e vírgula está sendo usado corretamente antes de "contudo":', 'O ponto e vírgula é usado antes de conjunções adversativas como "contudo".', 2),
('ponto-virgula-2', 'Qual frase apresenta o uso correto do ponto e vírgula em enumerações com itens que contêm vírgulas?', 'O ponto e vírgula é usado para separar itens em uma enumeração quando os itens já contêm vírgulas.', 2),
('ponto-virgula-2', 'Em qual alternativa o ponto e vírgula está sendo usado corretamente antes de "assim"?', 'O ponto e vírgula é usado antes de conjunções conclusivas como "assim".', 2),
('ponto-virgula-2', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente em um texto formal:', 'Em textos formais, o ponto e vírgula é usado para separar sentenças relacionadas mas independentes.', 2);
INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-3', 'Em qual alternativa o ponto e vírgula está sendo usado corretamente antes de "no entanto"?', 'O ponto e vírgula é usado antes de locuções conjuntivas adversativas como "no entanto".', 3),
('ponto-virgula-3', 'Qual frase apresenta o uso correto do ponto e vírgula em uma explicação extensa?', 'O ponto e vírgula é usado para separar explicações extensas relacionadas a um mesmo tema.', 3),
('ponto-virgula-3', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente para separar itens que formam grupos distintos:', 'O ponto e vírgula é usado para separar itens que formam grupos distintos em uma enumeração.', 3),
('ponto-virgula-3', 'Em qual alternativa o ponto e vírgula está sendo usado corretamente em um texto acadêmico?', 'Em textos acadêmicos, o ponto e vírgula é usado para separar diferentes argumentos ou evidências.', 3),
('ponto-virgula-3', 'Qual frase apresenta o uso correto do ponto e vírgula em uma citação complexa?', 'O ponto e vírgula é usado para separar diferentes partes de uma citação complexa.', 3);
INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-4', 'Em qual alternativa o ponto e vírgula está sendo usado corretamente para separar grupos de informações?', 'O ponto e vírgula é usado para separar grupos de informações relacionadas em uma enumeração complexa.', 4),
('ponto-virgula-4', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em uma descrição técnica:', 'Em descrições técnicas, o ponto e vírgula é usado para separar diferentes componentes ou aspectos.', 4),
('ponto-virgula-4', 'Qual opção apresenta o uso correto do ponto e vírgula em uma lista bibliográfica extensa?', 'Em listas bibliográficas extensas, o ponto e vírgula é usado para separar diferentes referências.', 4),
('ponto-virgula-4', 'Em qual alternativa o ponto e vírgula está sendo usado corretamente para separar elementos em uma tabela?', 'Em tabelas, o ponto e vírgula pode ser usado para separar elementos quando a vírgula já é usada em outro contexto.', 4),
('ponto-virgula-4', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente em uma legenda complexa:', 'Em legendas complexas, o ponto e vírgula é usado para separar diferentes explicações ou elementos.', 4);
INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-5', 'Em qual alternativa o ponto e vírgula está sendo usado corretamente em um relatório técnico?', 'Em relatórios técnicos, o ponto e vírgula é usado para separar diferentes seções ou tópicos relacionados.', 5),
('ponto-virgula-5', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em uma descrição científica:', 'Em descrições científicas, o ponto e vírgula é usado para separar diferentes características ou observações.', 5),
('ponto-virgula-5', 'Qual opção apresenta o uso correto do ponto e vírgula em um manual de instruções?', 'Em manuais de instruções, o ponto e vírgula é usado para separar diferentes etapas ou procedimentos.', 5),
('ponto-virgula-5', 'Em qual alternativa o ponto e vírgula está sendo usado corretamente em um texto legislativo?', 'Em textos legislativos, o ponto e vírgula é usado para separar diferentes disposições ou cláusulas.', 5),
('ponto-virgula-5', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente em uma prescrição médica:', 'Em prescrições médicas, o ponto e vírgula é usado para separar diferentes medicamentos ou instruções.', 5);
