INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-5', 'Identifique a alternativa em que o ponto e vírgula está sendo usado corretamente em um caso especial:', 'O ponto e vírgula tem usos especiais em diferentes contextos, como em documentos oficiais, textos jurídicos e científicos.', 5),
('ponto-virgula-5', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em um texto jurídico?', 'Em textos jurídicos, o ponto e vírgula é usado para separar considerandos ou itens em uma enumeração.', 5),
('ponto-virgula-5', 'Qual alternativa apresenta o uso correto do ponto e vírgula em um texto literário?', 'Em textos literários, o ponto e vírgula é usado para criar pausas mais longas que a vírgula, mas menos definitivas que o ponto.', 5),
('ponto-virgula-5', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em um texto científico:', 'Em textos científicos, o ponto e vírgula é usado para separar diferentes aspectos de um mesmo tema.', 5),
('ponto-virgula-5', 'Em qual frase o ponto e vírgula está sendo usado corretamente em um documento oficial?', 'Em documentos oficiais, o ponto e vírgula é usado para separar considerandos ou justificativas.', 5),
('ponto-virgula-5', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma citação direta?', 'Em citações diretas, o ponto e vírgula é usado para separar diferentes partes da citação.', 5),
('ponto-virgula-5', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente em um texto filosófico:', 'Em textos filosóficos, o ponto e vírgula é usado para separar diferentes argumentos ou ideias.', 5),
('ponto-virgula-5', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em um texto técnico?', 'Em textos técnicos, o ponto e vírgula é usado para separar diferentes especificações ou características.', 5),
('ponto-virgula-5', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma referência bibliográfica?', 'Em referências bibliográficas, o ponto e vírgula é usado para separar diferentes autores ou obras.', 5),
('ponto-virgula-5', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em um texto acadêmico:', 'Em textos acadêmicos, o ponto e vírgula é usado para separar diferentes argumentos ou evidências.', 5);
INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('prova-ponto-virgula', 'Identifique a alternativa em que o ponto e vírgula está sendo usado corretamente:', 'O ponto e vírgula é usado para separar orações coordenadas não unidas por conjunção, especialmente quando são extensas.', 5),
('prova-ponto-virgula', 'Em qual das opções o ponto e vírgula está sendo usado corretamente antes de conjunções adversativas?', 'O ponto e vírgula é usado antes de conjunções adversativas como "mas", "porém", "contudo", "entretanto".', 5),
('prova-ponto-virgula', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma enumeração complexa?', 'O ponto e vírgula é usado para separar itens em uma enumeração quando os itens já contêm vírgulas.', 5),
('prova-ponto-virgula', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente antes de expressões explicativas:', 'O ponto e vírgula pode ser usado antes de expressões explicativas como "isto é", "ou seja", "a saber".', 5),
('prova-ponto-virgula', 'Em qual frase o ponto e vírgula está sendo usado corretamente para separar orações coordenadas extensas?', 'O ponto e vírgula é usado para separar orações coordenadas extensas, especialmente quando já contêm vírgulas.', 5),
('prova-ponto-virgula', 'Qual alternativa apresenta o uso correto do ponto e vírgula antes de conjunções conclusivas?', 'O ponto e vírgula é usado antes de conjunções conclusivas como "logo", "portanto", "por isso".', 5),
('prova-ponto-virgula', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente em um texto jurídico:', 'Em textos jurídicos, o ponto e vírgula é usado para separar itens em uma enumeração ou considerandos.', 5),
('prova-ponto-virgula', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em uma lista bibliográfica?', 'Em listas bibliográficas, o ponto e vírgula é usado para separar diferentes referências.', 5),
('prova-ponto-virgula', 'Qual alternativa apresenta o uso correto do ponto e vírgula em um texto científico?', 'Em textos científicos, o ponto e vírgula é usado para separar diferentes aspectos de um mesmo tema.', 5),
('prova-ponto-virgula', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em um documento oficial:', 'Em documentos oficiais, o ponto e vírgula é usado para separar considerandos ou justificativas.', 5);
