INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-6', 'Em qual alternativa o ponto e vírgula está sendo usado corretamente em um poema?', 'Em poemas, o ponto e vírgula é usado para criar pausas rítmicas e separar imagens ou ideias relacionadas.', 6),
('ponto-virgula-6', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em um romance:', 'Em romances, o ponto e vírgula é usado para separar períodos relacionados ou criar transições suaves entre ideias.', 6),
('ponto-virgula-6', 'Qual alternativa apresenta o uso correto do ponto e vírgula em um ensaio acadêmico?', 'Em ensaios acadêmicos, o ponto e vírgula é usado para separar argumentos complexos ou exemplos relacionados.', 6),
('ponto-virgula-6', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em uma crônica?', 'Em crônicas, o ponto e vírgula é usado para criar pausas estilísticas ou separar observações relacionadas.', 6),
('ponto-virgula-6', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em uma monografia:', 'Em monografias, o ponto e vírgula é usado para separar diferentes aspectos de um mesmo tema ou conceito.', 6),
('ponto-virgula-6', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma dissertação?', 'Em dissertações, o ponto e vírgula é usado para separar premissas ou conclusões parciais de um raciocínio.', 6),
('ponto-virgula-6', 'Em qual frase o ponto e vírgula está sendo usado corretamente em um conto?', 'Em contos, o ponto e vírgula é usado para separar ações consecutivas ou criar transições na narrativa.', 6),
('ponto-virgula-6', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente em uma resenha crítica:', 'Em resenhas críticas, o ponto e vírgula é usado para separar diferentes aspectos analisados da obra.', 6),
('ponto-virgula-6', 'Qual alternativa apresenta o uso correto do ponto e vírgula em um artigo científico?', 'Em artigos científicos, o ponto e vírgula é usado para separar diferentes resultados ou conclusões relacionadas.', 6),
('ponto-virgula-6', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em uma tese?', 'Em teses, o ponto e vírgula é usado para separar diferentes linhas de argumentação ou evidências.', 6);
INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-7', 'Identifique a alternativa em que o ponto e vírgula está sendo usado corretamente em um relatório técnico:', 'Em relatórios técnicos, o ponto e vírgula é usado para separar diferentes seções ou conclusões relacionadas.', 7),
('ponto-virgula-7', 'Em qual frase o ponto e vírgula está sendo usado corretamente em um manual de instruções?', 'Em manuais de instruções, o ponto e vírgula é usado para separar diferentes etapas de um procedimento complexo.', 7),
('ponto-virgula-7', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma bula de remédio?', 'Em bulas de remédio, o ponto e vírgula é usado para separar diferentes efeitos colaterais ou precauções.', 7),
('ponto-virgula-7', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente em um artigo científico:', 'Em artigos científicos, o ponto e vírgula é usado para separar diferentes aspectos da metodologia ou resultados.', 7),
('ponto-virgula-7', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em uma patente?', 'Em patentes, o ponto e vírgula é usado para separar diferentes aspectos ou componentes da invenção.', 7),
('ponto-virgula-7', 'Qual alternativa apresenta o uso correto do ponto e vírgula em um laudo técnico?', 'Em laudos técnicos, o ponto e vírgula é usado para separar diferentes constatações ou conclusões.', 7),
('ponto-virgula-7', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em uma descrição de experimento:', 'Em descrições de experimentos, o ponto e vírgula é usado para separar diferentes etapas ou observações.', 7),
('ponto-virgula-7', 'Em qual frase o ponto e vírgula está sendo usado corretamente em um paper acadêmico?', 'Em papers acadêmicos, o ponto e vírgula é usado para separar diferentes citações ou referências a trabalhos anteriores.', 7),
('ponto-virgula-7', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma especificação técnica?', 'Em especificações técnicas, o ponto e vírgula é usado para separar diferentes requisitos ou características.', 7),
('ponto-virgula-7', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em um glossário técnico?', 'Em glossários técnicos, o ponto e vírgula é usado para separar diferentes acepções de um mesmo termo.', 7);
INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-8', 'Identifique a alternativa em que o ponto e vírgula está sendo usado corretamente em um contrato:', 'Em contratos, o ponto e vírgula é usado para separar diferentes condições ou cláusulas relacionadas.', 8),
('ponto-virgula-8', 'Em qual frase o ponto e vírgula está sendo usado corretamente em uma lei?', 'Em leis, o ponto e vírgula é usado para separar diferentes incisos ou condições de um mesmo artigo.', 8),
('ponto-virgula-8', 'Qual alternativa apresenta o uso correto do ponto e vírgula em um estatuto?', 'Em estatutos, o ponto e vírgula é usado para separar diferentes direitos ou deveres relacionados.', 8),
('ponto-virgula-8', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente em uma sentença judicial:', 'Em sentenças judiciais, o ponto e vírgula é usado para separar diferentes argumentos ou fundamentos da decisão.', 8),
('ponto-virgula-8', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em um código de conduta?', 'Em códigos de conduta, o ponto e vírgula é usado para separar diferentes normas ou princípios relacionados.', 8),
('ponto-virgula-8', 'Qual alternativa apresenta o uso correto do ponto e vírgula em um decreto?', 'Em decretos, o ponto e vírgula é usado para separar diferentes determinações ou considerações.', 8),
('ponto-virgula-8', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em um regulamento:', 'Em regulamentos, o ponto e vírgula é usado para separar diferentes regras ou disposições relacionadas.', 8),
('ponto-virgula-8', 'Em qual frase o ponto e vírgula está sendo usado corretamente em uma procuração?', 'Em procurações, o ponto e vírgula é usado para separar diferentes poderes ou autorizações concedidas.', 8),
('ponto-virgula-8', 'Qual alternativa apresenta o uso correto do ponto e vírgula em um acordo comercial?', 'Em acordos comerciais, o ponto e vírgula é usado para separar diferentes termos ou condições relacionadas.', 8),
('ponto-virgula-8', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em uma petição?', 'Em petições, o ponto e vírgula é usado para separar diferentes argumentos ou pedidos relacionados.', 8);
INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-9', 'Identifique a alternativa em que o ponto e vírgula está sendo usado corretamente em um caso complexo de coordenação:', 'O ponto e vírgula é usado para separar orações coordenadas complexas, especialmente quando já contêm vírgulas.', 9),
('ponto-virgula-9', 'Em qual frase o ponto e vírgula está sendo usado corretamente para separar orações com relações lógicas complexas?', 'O ponto e vírgula é usado para separar orações que possuem relações lógicas complexas entre si.', 9),
('ponto-virgula-9', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma estrutura de período misto?', 'Em períodos mistos, o ponto e vírgula é usado para separar grupos de orações relacionadas.', 9),
('ponto-virgula-9', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente em uma construção paralelística:', 'Em construções paralelísticas, o ponto e vírgula é usado para separar elementos paralelos complexos.', 9),
('ponto-virgula-9', 'Em qual das opções o ponto e vírgula está sendo usado corretamente para separar apostos longos?', 'O ponto e vírgula pode ser usado para separar apostos longos e complexos que já contêm vírgulas.', 9),
('ponto-virgula-9', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma enumeração com subdivisões complexas?', 'O ponto e vírgula é usado para separar itens em uma enumeração quando esses itens contêm subdivisões complexas.', 9),
('ponto-virgula-9', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente para separar orações com diferentes sujeitos e tempos verbais:', 'O ponto e vírgula é usado para separar orações com diferentes sujeitos e tempos verbais quando há uma relação de sentido entre elas.', 9),
('ponto-virgula-9', 'Em qual frase o ponto e vírgula está sendo usado corretamente em uma estrutura de causa e consequência complexa?', 'O ponto e vírgula é usado para separar orações em uma estrutura de causa e consequência complexa.', 9),
('ponto-virgula-9', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma construção com elipse de verbo?', 'O ponto e vírgula é usado para separar orações com elipse de verbo quando há outros elementos complexos.', 9),
('ponto-virgula-9', 'Em qual das opções o ponto e vírgula está sendo usado corretamente para separar orações justapostas com relações implícitas?', 'O ponto e vírgula é usado para separar orações justapostas com relações implícitas de causa, consequência ou oposição.', 9);
INSERT INTO exercises (category_id, question, explanation, difficulty) VALUES
('ponto-virgula-10', 'Identifique a alternativa que apresenta o uso correto do ponto e vírgula para separar orações coordenadas:', 'O ponto e vírgula é usado para separar orações coordenadas não unidas por conjunção, especialmente quando são extensas.', 10),
('ponto-virgula-10', 'Em qual frase o ponto e vírgula está sendo usado corretamente antes de conjunções adversativas?', 'O ponto e vírgula é usado antes de conjunções adversativas como "mas", "porém", "contudo", "entretanto".', 10),
('ponto-virgula-10', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma enumeração complexa?', 'O ponto e vírgula é usado para separar itens em uma enumeração quando os itens já contêm vírgulas.', 10),
('ponto-virgula-10', 'Identifique a opção em que o ponto e vírgula está sendo usado corretamente antes de expressões explicativas:', 'O ponto e vírgula pode ser usado antes de expressões explicativas como "isto é", "ou seja", "a saber".', 10),
('ponto-virgula-10', 'Em qual das opções o ponto e vírgula está sendo usado corretamente em um texto técnico?', 'Em textos técnicos, o ponto e vírgula é usado para separar diferentes especificações ou características.', 10),
('ponto-virgula-10', 'Qual alternativa apresenta o uso correto do ponto e vírgula em um documento oficial?', 'Em documentos oficiais, o ponto e vírgula é usado para separar considerandos ou justificativas.', 10),
('ponto-virgula-10', 'Identifique a frase em que o ponto e vírgula está sendo usado corretamente em um texto literário:', 'Em textos literários, o ponto e vírgula é usado para criar pausas mais longas que a vírgula, mas menos definitivas que o ponto.', 10),
('ponto-virgula-10', 'Em qual frase o ponto e vírgula está sendo usado corretamente em uma citação direta?', 'Em citações diretas, o ponto e vírgula é usado para separar diferentes partes da citação.', 10),
('ponto-virgula-10', 'Qual alternativa apresenta o uso correto do ponto e vírgula em uma estrutura com elipse?', 'O ponto e vírgula é usado para separar orações com elipse de algum termo quando há outros elementos complexos.', 10),
('ponto-virgula-10', 'Em qual das opções o ponto e vírgula está sendo usado corretamente para separar orações com relações implícitas?', 'O ponto e vírgula é usado para separar orações com relações implícitas de causa, consequência ou oposição.', 10);
